    BlazoredTextEditor QuillHtml;
    BlazoredTextEditor QuillNative;
    string QuillHTMLContent;
    [Parameter]
    public string Title { get; set; }

    public async void SavePost()
    {
        QuillHTMLContent = await this.QuillHtml.GetHTML();
        var newPost = new BlogPost()
        {
            Title = Title,
            Author = "Joe Bloggs",
            Post = QuillHTMLContent,
            Posted = DateTime.Now
        };
        var content = await _httpClient.PostAsJsonAsync<BlogPost>("/api/AddBlogPost", newPost).Result.Content.ReadAsStringAsync();
        var id = JsonSerializer.Deserialize<BlogPost>(content).Id;

        navigationManager.NavigateTo($"viewpost/{id}");
    }
    public async void InsertImage()
    {
        await this.QuillNative.InsertImage("images/BlazorHelpWebsite.gif");
        StateHasChanged();
    }

